    cue_word_occurrences = {}
    for cue_word in cue_words:
        cue_word_occurrences[cue_word] = 0
    for word in input_text.split():
        if word in cue_words:
            cue_word_occurrences[cue_word] += 1
    # Step 2: Analyze Causal Relationships
    causal_relationships = []
    for word in input_text.split():
        if word in cue_word_occurrences:
            causal_relationships.append((cue_word_occurrences[word], word))
    # Step 3: Draw Conclusions
    conclusions = []
    for (cue_word_occurrence, word) in causal_relationships:
        if cue_word_occurrence > 0:
            conclusions.append(f"{word} causes {word}")
    return conclusions

